SELECT x.YY, x.MM, x.DD, NmYpZM01, Bott, [Bott]*[Bott2SC] AS StdCase, [Bott]/[BtlPerCs] AS ActCase, Size, StdCaseSize, BtlPerCs, x.Val, x.Sku, g.DesSKU, Dv, PH, CdTopaz, b.CdB AS [Quality Code], b.NmB AS Quality, d.CdSHBrand AS [Brand Code], NmSHBrand AS Brand, NmFinStream AS Stream, NmHse AS House, SnoHse, SnoFinStream, SnoSHBrand
FROM ((((((((OHZM01 AS x INNER JOIN YMDCur AS z ON (x.DD=z.DD) AND (x.MM=z.MM) AND (x.YY=z.YY)) LEFT JOIN YpZM01 AS a ON x.YpZM01      = a.YpZM01) LEFT JOIN q1SKU AS b ON x.Sku         = b.Sku) LEFT JOIN SHBrandQ AS c ON b.CdB         = c.CdQly) LEFT JOIN SHBrand AS d ON c.CdSHBrand   = d.CdSHBrand) LEFT JOIN FinStream AS e ON d.CdFinStream = e.CdFinStream) LEFT JOIN Hse AS f ON c.Hse         = f.Hse) LEFT JOIN SKU AS g ON x.SKU         = g.SKU) LEFT JOIN Topaz AS h ON g.Topaz       = h.Topaz
ORDER BY SnoFinStream, NmFinStream, SnoHse, NmHse, SnoSHBrand, NmSHBrand, b.NmB;
